    function setTopImage(){
        
        var now = new Date();
        var curr_h = now.getHours();
        if(curr_h >= '01' && curr_h < '11'){
            $('img#morning1').addClass('active');
        } else if(curr_h >= 11 && curr_h < 13){
            $('img#midday1').addClass('active');
        } else if(curr_h >= 13 && curr_h < 20){
            $('img#afternoon1').addClass('active');
        } else {
            $('img#evening1').addClass('active');
        }
    }
    
    function swapImages(){
        var $active = $('#top-gallery .active');
        var $next = ($('#top-gallery .active').next().length > 0) ? $('#top-gallery .active').next() : $('#top-gallery img:first');
        $active.fadeOut(function(){
        $active.removeClass('active');
        $next.fadeIn().addClass('active');
        });
      }
    
    function swapInnerImages(){
        var fadeTime = 2000;
    
        var $active = $('#top-gallery .active');
