        },5000);
     
      }
      
      
      
    function checkDate(){

      var now = new Date();
      var curr_h = now.getHours();
      var curr_m = now.getMinutes();
      var curr_s = now.getSeconds();
      if(curr_h == '01' && curr_m == '00' && curr_s == '00'){
          swapImages();
      } else if (curr_h == '11' && curr_m == '00' && curr_s == '00'){
          swapImages();
      } else if (curr_h == '13' && curr_m == '00' && curr_s == '00'){
          swapImages();
      } else if (curr_h == '20' && curr_m == '00' && curr_s == '00'){
          swapImages();
      }
    }
    
    
 
